// 38. Count and Say
// https://leetcode.com/problems/count-and-say/
function countAndSay(n: number): string {
  function recursion(string: string): string {
    let lastChar = string[0];
    let lastCharStreak = 0;
    let result = "";

    for (let i = 0; i < string.length; ++i) {
      if (string[i] === lastChar) {
        lastCharStreak += 1;

        continue;
      } else {
        result += `${lastCharStreak}${lastChar}`;

        lastChar = string[i];
        lastCharStreak = 1;
      }
    }

    result += `${lastCharStreak}${lastChar}`;

    return result;
  }

  let result = "1";

  for (let i = 1; i < n; ++i) {
    result = recursion(result);
  }

  return result;
}

describe("38. Count and Say", () => {
  test("#1", () => {
    expect(countAndSay(1)).toBe("1");
  });

  test("#2", () => {
    expect(countAndSay(2)).toBe("11");
  });

  test("#3", () => {
    expect(countAndSay(3)).toBe("21");
  });

  test("#4", () => {
    expect(countAndSay(4)).toBe("1211");
  });

  test("#5", () => {
    expect(countAndSay(5)).toBe("111221");
  });

  test("#6", () => {
    expect(countAndSay(10)).toBe("13211311123113112211");
  });

  test("#7", () => {
    expect(countAndSay(30)).toBe(
      ""
    );
  });
});
